import React, { useEffect, useState } from 'react';
import './App.css';
import { Line, LineChart } from 'recharts';
class App extends React.Component {
	// Constructor
	constructor(props) {
		super(props);
		this.state = {
			items: [],
			DataisLoaded: false
		};
	}
	// ComponentDidMount is used to
	// execute the code
	// 取得排序資料的api /orderdata
	componentDidMount() {
		fetch("http://localhost:5001/api")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return 
      <div>
			  <h1> no no conmou </h1> 
      </div> ;
    // const sort = items.sort(sortData)
		return (
      <div className = "App">
        <h1> get api success </h1> {
          items.map((item) => (
          <ol key = { item.id } >
            temp: { item.temp },
            hunidity: { item.hunidity},
            date: { item.date }
            </ol>
          ))
        }
      <LineChart width={600} height={300} data={ item.temp }>
        <Line type="monotone" dataKey='temp' stroke='#2196F3' />
      </LineChart>
      </div>
	  );
  }
}

export default App;
