import React, { useEffect, useState } from 'react';
import './App.css';
import { CartesianAxis, CartesianGrid, Legend, Line, LineChart, XAxis, YAxis, Tooltip } from 'recharts';
//import { Tooltip } from 'chart.js';

// const data = [
//   { name: "2017", react: 32, angular: 37, vue: 60 },
//   { name: "2018", react: 42, angular: 42, vue: 54 },
//   { name: "2019", react: 51, angular: 41, vue: 54 },
//   { name: "2020", react: 60, angular: 37, vue: 28 },
//   { name: "2021", react: 51, angular: 31, vue: 27 },
//   { name: "2022", react: 95, angular: 44, vue: 49 },
// ];
class App extends React.Component {
	// Constructor
	constructor(props) {
		super(props);
		this.state = {
			items: [],
			DataisLoaded: false
		};
	}

	componentDidMount() {
		fetch("http://localhost:5001/api")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}

	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return 
			<div>
				<h1> no no conmou </h1> 
			</div> ;
		// const sort = items.sort(sortData)
		return (
			<div className = "App"  >
				<h1> get api success </h1> {
				items.map((item) => (
				<ol key = { item.id } >
					temp: { item.temp },
					hunidity: { item.hunidity},
					date: { item.date }
				</ol>
				))
				}
				<LineChart className="chart" width={600} height={300} data={ items } margin={{ top: 5, right: 20, bottom: 5, left: 0 }}>
					<Line type="monotone" dataKey='temp' stroke='#2196F3' strokeWidth={2}/>
					<Line type="monotone" dataKey='hunidity' stroke='#FFCA29' strokeWidth={2}/>
					<CartesianGrid stroke='#ccc' strokeDasharray="5 5"/>
					<XAxis dataKey='date'/>
					<YAxis/>
					<Tooltip/>
					<Legend/>
				</LineChart>
				<p>溫度：{items[3].temp}</p>
				<p>濕度：{items[3].hunidity}</p>
			</div>
		);
  }
}

