import React, { useEffect, useState } from 'react';
import './App.css';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

class App extends React.Component {
	// Constructor
	constructor(props) {
		super(props);
		this.state = {
			items: [],
			DataisLoaded: false
		};
	}
	// ComponentDidMount is used to
	// execute the code
	// 取得排序資料的api /orderdata
	componentDidMount() {
		fetch("http://localhost:5001/api")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}

	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return 
      <div>
			  <h1> no no conmou </h1> 
      </div> ;
    // const sort = items.sort(sortData)
		return (
      <div className = "App">
        <h1> get api success </h1> {
          items.map((item) => (
          <ol key = { item.id } >
            temp: { item.temp },
            hunidity: { item.hunidity},
            date: { item.date }
            </ol>
          ))
        }
      </div>
	  );
  }
}
function chartdata() {
  const [chartData, setChartData] = useState({
    datasets: [],
  });

  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    setChartData({
      labels: ["John", "Kevin", "Geroge", "Micheal", "Oreo"],
      datasets: [
        {
          label: "Whom'st let the dogs out",
          data: [12, 55, 34, 120, 720],
          borderColor: "rgb(53, 162, 235)",
          backgroundColor: "rgba(53, 162, 235, 0.4)",
        },
      ],
    });
    setChartOptions({
      responsive: true,
      plugins: {
        legend: {
          position: "top",
        },
        title: {
          display: true,
          text: "Whom'st let the dogs out",
        },
      },
    });
  }, []);

  return (
    <div className="App">
      <Bar options={chartOptions} data={chartData} />
    </div>
  );
  }
export default App;
