function sortby(a,b){
  return a.temp-b.temp;
}
function sortData(dataArray){
  return dataArray.sort(function(a,b){
    return Date.parse(b.date.replace(/-/g,"/")) - Date.parse(a.date.replace(/-/g,"/"))
  });
}
class App extends React.Component {
	// Constructor
	constructor(props) {
		super(props);
		this.state = {
			items: [],
			DataisLoaded: false
		};
	}
	// ComponentDidMount is used to
	// execute the code
	// 取得排序資料的api /orderdata
	componentDidMount() {
		fetch("http://localhost:5001/api")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}

	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return 
      <div>
			  <h1> no no conmou </h1> 
      </div> ;
    // const sort = items.sort(sortData)
		return (
      <div className = "App">
        <h1> get api success </h1> {
          items.map((item) => (
          <ol key = { item.id } >
            temp: { item.temp },
            hunidity: { item.hunidity},
            date: { item.date }
            </ol>
          ))
        }
      </div>
	  );
  }
}

class BarGraph extends React.Component {
  chartRef = React.createRef();

  componentDidMount() {
      const myChartRef = this.chartRef.current.getContext("2d");

      new Chart(myChartRef, {
          type: "bar",
          data: {
              labels: ["Jan", "Feb", "March", "Apr", "May", "June", "July", "Aug", "Sep", "Oct", "Nov", "Dec"],
              datasets: [
                  {
                      label: "Sales",
                      data: [61, 134, 83, 77, 49, 61, 90, 33, 54, 94, 93, 62],
                  }
              ]
          },
          options: {
          }
      });
  }
  render() {
      return (
          <div>
            <canvas
              id="myChart"
              ref={this.chartRef}
            />
          </div>
      )
  }
}
export default App
